import React, { useRef } from 'react';
import './TransactionBubble.css';

type Props = {
    size: number
}

class TransactionBubble extends React.Component<Props> {
    ref = useRef()
    windowWidth: number = 0
    windowHeight: number = 0
    bubbleWidth: number = 0
    bubbleHeight: number = 0
    leftPercent: number = 0
    bubbleScale: number = 0

    constructor(props: any) {
        super(props)
        this.setWindowDimensions()
        console.log('width:', this.windowWidth, 'height:', this.windowHeight)

        // Calculate size of bubble
        const min = 0.0001
        const max = 0.05
        // percent = size / (A + (B - A))
        this.bubbleScale = Number.parseFloat((Math.max(props.size, min) / (min + (max - min))).toFixed(1))
        console.log('scale:', this.bubbleScale)

        // Calculate start X
        const startX: number = Math.floor(Math.random() * this.windowWidth)
        this.leftPercent = Math.floor(startX / this.windowWidth * 80)
        console.log('left:', this.leftPercent)
    }

    componentDidMount() {
        window.addEventListener('resize', this.setWindowDimensions);
        this.bubbleWidth = this.ref.current?.clientWidth || 0

    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.setWindowDimensions);
    }

    setWindowDimensions() {
        this.windowWidth = document.documentElement?.clientWidth || document.body?.clientWidth || 0
        this.windowHeight = document.documentElement?.clientHeight || document.body?.clientHeight || 0
    }

    render() {
        return (
            <div className="bubble flex flex-col place-items-center justify-center"
                style={{
                    left: `${this.leftPercent}%`,
                    transform: `scale(${this.bubbleScale})`
                }}>
                <span className='transaction-amount mb-2 text-2xl'>#.###### BTC</span>
                <span className='transaction-price mt-2 text-xl'>$#####.##</span>
            </div>
        );
    }
}

export default TransactionBubble;
