import React, { useEffect, useRef, useState } from 'react';
import { Transaction } from '../../models/transaction'
import './TransactionBubble.css';

interface BubbleStyle {
    bubbleSize: number;
    amountFontSize: string;
    priceFontSize: string;
}

const sizeMap: { [key: string]: BubbleStyle } = {
    0.000499: { bubbleSize: 50, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' },
    0.000999: { bubbleSize: 75, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' },
    0.00499: { bubbleSize: 100, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' },
    0.00999: {bubbleSize: 150, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' },
    0.09: { bubbleSize: 250, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' },
    0.3: { bubbleSize: 500, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' },
    "0100": { bubbleSize: 750, amountFontSize: 'text-2xl', priceFontSize: 'text-xl' }
}

function TransactionBubble({ transaction = {} as Transaction }) {
    // const bubbleRef = useRef()
    const [canvasWidth, setCanvasWidth] = useState(document.documentElement?.clientWidth || document.body?.clientWidth || 0)
    const [canvasHeight, setCanvasHeight] = useState(document.documentElement?.clientHeight || document.body?.clientHeight || 0)
    const [bubbleSize, setBubbleSize] = useState(0)
    const [random] = useState(Math.random())
    const [position] = useState(random * canvasWidth)
    const [marginTop] = useState(canvasHeight + bubbleSize)

    useEffect(() => {
        // Determine size of bubble
        if (transaction != null) {
            for (let cutoffString of Object.keys(sizeMap)) {
                const cutoffAmount = Number.parseFloat(cutoffString)
                if (transaction.amount <= cutoffAmount) {
                    setBubbleSize(sizeMap[cutoffAmount].bubbleSize)
                    break;
                }
            }
        }
    }, [transaction])

    const getWindowSize = () => {
        setCanvasWidth(document.documentElement?.clientWidth || document.body?.clientWidth || 0)
        setCanvasHeight(document.documentElement?.clientHeight || document.body?.clientHeight || 0)
        console.log('canvas:', canvasWidth, 'x', canvasHeight)
    }

    useEffect(() => {
        window.addEventListener('resize', getWindowSize)
        return () => window.removeEventListener('resize', getWindowSize)
    }, [])

    return (
        <div className="bubble flex flex-col place-items-center justify-center"
            style={{
                left: `${position}px`,
                width: `${bubbleSize}px`,
                height: `${bubbleSize}px`,
                // marginTop: `${marginTop}px`,
                // animation: 'sideWays 2s ease-in-out infinite alternate'
            }}>
            <span className='transaction-amount mb-2 text-2xl'>{transaction?.amount} {transaction?.coin}</span>
            <span className='transaction-price mt-2 text-xl'>${transaction?.price}</span>
        </div>
    );
}

export default TransactionBubble;
