import React from 'react';
import './TransactionBubble.css';

type Props = {
    size: number
}

class TransactionBubble extends React.Component<Props> {
    windowWidth: number = 0
    startX: number = 0
    size: number = 0


    constructor(props: any) {
        super(props)
        this.size = props.size
        this.setWindowWidth()

        // Calculate start X
        this.startX = Math.floor(Math.random() * this.windowWidth)
        console.log(this.startX)
    }

    componentDidMount() {
        window.addEventListener('resize', this.setWindowWidth);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.setWindowWidth);
    }

    setWindowWidth() {
        this.windowWidth = document.documentElement?.clientWidth || document.body?.clientWidth || 0
    }

    render() {
        return (
            <div id='bubbleContainer' className='bubble-container relative w-64 h-64'>
                <div className={`bubble-background
                w-full h-full rounded-full overflow-hidden
                flex flex-col place-items-center justify-center`}>
                    <span className='transaction-amount mb-2 text-2xl'>#.###### BTC</span>
                    <span className='transaction-price mt-2 text-xl'>$#####.##</span>
                </div>
                <div className='bubble-body w-full h-full rounded-full'></div>
            </div>
        );
    }
}

export default TransactionBubble;
