import { useState } from 'react';
import { HiOutlineMenu } from 'react-icons/hi';
import './Menu.css';

function Menu() {
    const [showing, setShowing] = useState(false);

    const toggleMenu = () => {
        setShowing(!showing);
    }

    return (
        <>
        {showing && <div
        className={`absolute bg-menu drop-shadow top-1.5 left-1.5 w-80 h-64 rounded flex place-items-center justify-center`}>
            <h1>Menu</h1>
        </div>}
        <div className={`absolute top-1.5 left-1.5 w-12 h-12
            rounded flex place-items-center justify-center cursor-pointer
            ${showing ? 'bg-coolgray100 border' : 'bg-coolgray50 drop-shadow-lg'}`}
            onClick={toggleMenu}>
            <HiOutlineMenu className='w-6 h-6' />
        </div>
        </>

    );
}

export default Menu;
