import './Canvas.css';
import TransactionBubble from '../TransactionBubble/TransactionBubble';
import { useEffect, useState } from 'react';
import { Transaction } from '../../models/transaction';

function Canvas() {
    const [nextKey, setNextKey] = useState(0)
    const [transactionsMap] = useState(new Map<number, Transaction>())
    // const [timers, setTimers] = useState([] as NodeJS.Timeout[])
    // const [transactions, setTransactions] = useState([])

    const deleteBubble = (key: number) => {
        console.log('deleting bubble:', key)
        transactionsMap.delete(key)
    }

    useEffect(() => {
        transactionsMap.set(nextKey, {
            amount: 0.00765,
            coin: 'BTC',
            exchange: 'Coinbase',
            price: 0,
        })
        setTimeout(() => deleteBubble(nextKey), 5000)
        // let currentTimers = [...timers]
        // currentTimers.push(setTimeout(() => transactionsMap.delete(nextKey), 5000))
        // setTimers(currentTimers)

        transactionsMap.set(nextKey+1, {
            amount: 0.01234,
            coin: 'BTC',
            exchange: 'Coinbase',
            price: 0,
        })
        setTimeout(() => deleteBubble(nextKey+1), 5000)
        // currentTimers = [...timers]
        // currentTimers.push(setTimeout(() => transactionsMap.delete(nextKey), 5000))
        // setTimers(currentTimers)
        console.log(transactionsMap)

        setNextKey(nextKey + 2)
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <div className='w-screen h-screen bg-coolgray400'>
            {Array.from(transactionsMap.values()).map((transaction, i) => (
                <TransactionBubble key={i} transaction={transaction} />
            ))}
        </div>
    );
}

export default Canvas;
