import React, { useEffect, useRef, useState } from 'react';
import './TransactionBubble.css';

// class TransactionBubble extends React.Component<Props> {
function TransactionBubble({ size = 0 }) {
    // const bubbleRef = useRef()
    const [canvasWidth, setCanvasWidth] = useState(document.documentElement?.clientWidth || document.body?.clientWidth || 0)
    const [canvasHeight, setCanvasHeight] = useState(document.documentElement?.clientHeight || document.body?.clientHeight || 0)
    const [bubbleSize, setBubbleSize] = useState(0)
    const [position, setPosition] = useState(0)
    const [random, setRandom] = useState(Math.random())

    // Calculate size of bubble
    const min = 0.0001
    const max = 0.05
    const maxPixels = 1000
    const ratio = Number.parseFloat((Math.max(size, min) / (min + (max - min))).toFixed(1))
    setBubbleSize(100 + ratio * maxPixels)

    const getWindowSize = () => {
        setCanvasWidth(document.documentElement?.clientWidth || document.body?.clientWidth || 0)
        setCanvasHeight(document.documentElement?.clientHeight || document.body?.clientHeight || 0)
    }

    // useEffect(() => {
    //     window.addEventListener('resize', getWindowSize)
    //     // return () => window.removeEventListener('resize', getWindowSize)
    // }, [])

    // Position bubble within canvas
    // useEffect(() => {
    //     console.log('canvas:', canvasWidth, 'x', canvasHeight)

    //     const availablePixels = canvasWidth - bubbleSize
    //     console.log('Position:', random * availablePixels)
    //     setPosition(random * availablePixels)
    // }, [canvasWidth, canvasHeight, bubbleSize, random])

    return (
        <div className="bubble flex flex-col place-items-center justify-center"
            style={{
                left: `${position}px`,
                width: `${bubbleSize}px`,
                height: `${bubbleSize}px`,
                // transform: `scale(${this.bubbleScale})`
            }}>
            <span className='transaction-amount mb-2 text-2xl'>#.###### BTC</span>
            <span className='transaction-price mt-2 text-xl'>$#####.##</span>
        </div>
    );
}

export default TransactionBubble;
