import { useEffect } from 'react';
import './TransactionBubble.css';

function TransactionBubble({ size = 0 }) {
    console.log(size)

    const getWidth = () => {        
        if (document.documentElement && document.documentElement.clientWidth) {
            return document.documentElement.clientWidth;
        }

        if (document.body) {
            return document.body.clientWidth;
        }
    }

    useEffect(() => {
        console.log('width:', window.screen.availWidth)
    }, [window.screen.availWidth])

    return (
        <div className='bubble-container relative top-5 left-5 w-64 h-64'>
            <div className={`bubble-background
            w-full h-full rounded-full overflow-hidden
            flex flex-col place-items-center justify-center`}>
                <span className='transaction-amount mb-2 text-2xl'>#.###### BTC</span>
                <span className='transaction-price mt-2 text-xl'>$#####.##</span>
            </div>
            <div className='bubble-body w-full h-full rounded-full'></div>
        </div>
    );
}

export default TransactionBubble;
