import './Canvas.css';
import TransactionBubble from '../TransactionBubble/TransactionBubble';
import { useEffect, useState } from 'react';
import { Transaction } from '../../models/transaction';

function Canvas() {
    const [nextKey, setNextKey] = useState(0)
    const [transactionsMap] = useState(new Map<number, Transaction>())
    const [transactions, setTransactions] = useState({} as {[key: string]: Transaction})
    const [displayedTransactions, setDisplayedTransactions] = useState([] as Transaction[])
    // const [timers, setTimers] = useState([] as NodeJS.Timeout[])

    const deleteBubble = (key: string) => {
        console.log('deleting bubble:', key)
        // transactionsMap.delete(key)
        if (Object.keys(transactions).includes(key)) {
            delete transactions[key]
        }
        setTransactions({...transactions})
    }

    useEffect(() => {
        // transactionsMap.set(nextKey, {
        //     amount: 0.00765,
        //     coin: 'BTC',
        //     exchange: 'Coinbase',
        //     price: 0,
        // })
        transactions[nextKey.toString()] = {
            amount: 0.00765,
            coin: 'BTC',
            exchange: 'Coinbase',
            price: 0,
        }
        setTimeout(() => deleteBubble(nextKey.toString()), 10000)

        transactions[(nextKey+1).toString()] = {
            amount: 0.00765,
            coin: 'BTC',
            exchange: 'Coinbase',
            price: 0,
        }
        setTimeout(() => deleteBubble((nextKey+1).toString()), 10000)

        console.log(transactions)

        setNextKey(nextKey + 2)
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    useEffect(() => {
        console.log('CHANGED')
    }, [transactions])

    return (
        <div className='w-screen h-screen bg-coolgray400'>
            {Object.entries(transactions).map(([key, transaction]) => (
                <TransactionBubble key={key} transaction={transaction} />
            ))}
        </div>
    );
}

export default Canvas;
