import React, { useEffect, useRef, useState } from 'react';
import './TransactionBubble.css';

// class TransactionBubble extends React.Component<Props> {
function TransactionBubble({ size = 0 }) {
    const bubbleRef = useRef()
    const [canvasWidth, setCanvasWidth] = useState(0)
    const [canvasHeight, setCanvasHeight] = useState(0)
    const [bubbleSize, setBubbleSize] = useState(0)
    const [position, setPosition] = useState(0)
    const random = Math.random()

    // Calculate size of bubble
    const min = 0.0001
    const max = 0.05
    setBubbleSize(Number.parseFloat((Math.max(size, min) / (min + (max - min))).toFixed(1)))

    const getWindowSize = () => {
        setCanvasWidth(document.documentElement?.clientWidth || document.body?.clientWidth || 0)
        setCanvasHeight(document.documentElement?.clientHeight || document.body?.clientHeight || 0)
    }

    useEffect(() => {
        window.addEventListener('resize', getWindowSize)
        return () => window.removeEventListener('resize', getWindowSize)
    }, [])

    useEffect(() => {
        const maxRadiusPx = 1000
        const newPosition = 100 + (random * maxRadiusPx)
        console.log('Position:', newPosition)
        setPosition(newPosition)
    }, [random])

    useEffect(() => {
        console.log('canvas:', canvasWidth, 'x', canvasHeight)
    }, [canvasWidth, canvasHeight])

    return (
        <div ref={bubbleRef} className="bubble flex flex-col place-items-center justify-center"
            style={{
                left: `${position}px`,
                width: `${bubbleRadius * 2}px`,
                height: `${bubbleRadius * 2}px`,
                // transform: `scale(${this.bubbleScale})`
            }}>
            <span className='transaction-amount mb-2 text-2xl'>#.###### BTC</span>
            <span className='transaction-price mt-2 text-xl'>$#####.##</span>
        </div>
    );
}

export default TransactionBubble;
