import React, { useEffect, useRef, useState } from 'react';
import './TransactionBubble.css';

type Props = {
    size: number
}

// class TransactionBubble extends React.Component<Props> {
function TransactionBubble ({ size = 0 }) {
    const bubbleRef = useRef()
    const [width, setWidth] = useState(0)
    const [height, setHeight] = useState(0)
    const [bubbleRadius, setBubbleRadius] = useState(0)
    const [left, setLeft] = useState(0)
    const randomPosition = Math.random()

    // Calculate size of bubble
    const min = 0.0001
    const max = 0.05
    const bubbleRatio = Number.parseFloat((Math.max(size, min) / (min + (max - min))).toFixed(1))
    setBubbleRadius(bubbleRatio )

    const getWindowSize = () => {
        setWidth(document.documentElement?.clientWidth || document.body?.clientWidth || 0)
        setHeight(document.documentElement?.clientHeight || document.body?.clientHeight || 0)
    }

    useEffect(() => {
        window.addEventListener('resize', getWindowSize)
        return () => window.removeEventListener('resize', getWindowSize)
    }, [])

    useEffect(() => {
        const maxRadiusPx = 1000
        setLeft(100 + (randomPosition * maxRadiusPx))
    }, [bubbleRadius, randomPosition])

    useEffect(() => {
        console.log('width:', width, 'height:', height)
    }, [width, height])

    // setWindowDimensions() {
    //     this.windowWidth = document.documentElement?.clientWidth || document.body?.clientWidth || 0
    //     this.windowHeight = document.documentElement?.clientHeight || document.body?.clientHeight || 0
    // }

    return (
        <div className="bubble flex flex-col place-items-center justify-center"
            style={{
                left: `${left}px`,
                width: `${bubbleRadius * 2}px`,
                height: `${bubbleRadius * 2}px`,
                // transform: `scale(${this.bubbleScale})`
            }}>
            <span className='transaction-amount mb-2 text-2xl'>#.###### BTC</span>
            <span className='transaction-price mt-2 text-xl'>$#####.##</span>
        </div>
    );
}

export default TransactionBubble;
